$(document).ready(function () { $(".basic-single").select2(); $('.timepicker').daterangepicker({ singleDatePicker: true, timePicker: true, timePicker24Hour: false, "locale": { "format": "hh:mm A" } }).on('show.daterangepicker', function (ev, picker) { picker.container.find(".calendar-table").hide(); picker.container.find('.calendar-time').css('margin-right', '15px') }); $('.datetimepicker').daterangepicker({ singleDatePicker: true, showDropdowns: true, minYear: 1901, "drops": "up", locale: { format: 'YYYY-MM-DD' }, maxYear: parseInt(moment().format('YYYY'), 10) }, function (start, end, label) { var years = moment().diff(start, 'years'); }); $('.datetimepickerwd').daterangepicker({ singleDatePicker: true, "timePicker": true, showDropdowns: true, "timePicker24Hour": true, minYear: 1901, "drops": "up", locale: { format: 'YYYY-MM-DD HH:mm' }, maxYear: parseInt(moment().format('YYYY'), 10) }, function (start, end, label) { var years = moment().diff(start, 'years'); }); $('.form-check-input').bootstrapToggle(); $('.skin-minimal .i-check input').iCheck({ checkboxClass: 'icheckbox_minimal', radioClass: 'iradio_minimal', increaseArea: '20%' }); $('.skin-square .i-check input').iCheck({ checkboxClass: 'icheckbox_square-green', radioClass: 'iradio_square-green' }); $('.skin-flat .i-check input').iCheck({ checkboxClass: 'icheckbox_flat-red', radioClass: 'iradio_flat-red' }); $('.skin-line .i-check input').each(function () { var self = $(this), label = self.next(), label_text = label.text(); label.remove(); self.iCheck({ checkboxClass: 'icheckbox_line-blue', radioClass: 'iradio_line-blue', insert: '<div class="icheck_line-icon"></div>' + label_text }); }); });